const container=document.querySelector(".services-inside__text"),blueLine=container.querySelector(".blue-line"),h1=container.querySelector("h1");let styles=h1.currentStyle||window.getComputedStyle(h1),containerStyles=container.currentStyle||window.getComputedStyle(container);function blueLineHeight(){if(window.innerWidth>1024){let e=h1.offsetHeight/2+5;blueLine.style.height=`calc(${e}px + ${styles.marginBottom})`,blueLine.style.width=`calc(${containerStyles.paddingLeft} + 25px)`,blueLine.style.left=`calc(-${containerStyles.paddingLeft} / 2)`}}blueLineHeight(),window.addEventListener("resize",(()=>{blueLineHeight()}));